CREATE TABLE IF NOT EXISTS users (id SERIAL PRIMARY KEY,name VARCHAR (100) NOT NULL UNIQUE,password VARCHAR (8) NOT NULL,created_at TIMESTAMP);
CREATE TABLE IF NOT EXISTS post  (id SERIAL PRIMARY KEY,text VARCHAR (100) NOT NULL,created_at TIMESTAMP,user_id INT NOT NULL REFERENCES users(id));
CREATE TABLE IF NOT EXISTS comment (id SERIAL PRIMARY KEY, text VARCHAR (100) NOT NULL, post_id INT NOT NULL REFERENCES post(id),user_id INT NOT NULL REFERENCES users(id),created_at TIMESTAMP);
CREATE TABLE IF NOT EXISTS likes (id SERIAL PRIMARY KEY,user_id INT NOT NULL REFERENCES users(id),post_id INT REFERENCES post(id),comment_id INT REFERENCES comment (id));